INSERT INTO `wp_posts` VALUES(205, 1, '2015-03-20 20:24:48', '2015-03-20 20:24:48', '<h1>Changing the Chicago vending landscape.</h1>\r\n<p class="p1"><span class="s1">Leangreen Vending sets the standard in Chicago for how vending ought to be.</span> We''re what happens when you blend good ol'' fashioned customer care with the latest and greatest vending solutions. Our laser-like focus is on customer service and product selection. When it comes to product options, Leangreen stands apart from the other companies because of our emphasis on healthier options.</p>\r\n\r\n<h3>Dig a little deeper</h3>', 'Home', '', 'inherit', 'closed', 'open', '', '6-revision-v1', '', '', '2015-03-20 20:24:48', '2015-03-20 20:24:48', '', 6, 'http://leangreen.wyattmeade.com/2015/03/6-revision-v1/', 0, 'revision', '', 0);
